mapper.update:
UPDATE address
SET is_delete = 1
where id = #{id}

UPDATE address
SET name        = #{name},
    mobile      = #{mobile},
    province_id = #{provinceId},
    city_id     = #{cityId},
    district_id = #{districtId},
    address     = #{address},
    is_default  = #{isDefault}
WHERE id = #{id}
  AND user_id = #{userId}
