mapper.update:
UPDATE cart
SET is_delete = 1
WHERE user_id = #{userId}
  AND is_delete = 0
  AND product_id IN #{productIds}

update.foreach:
UPDATE cart
SET number = #{cart.number},
add_price = #{cart.retailPrice}
WHERE product_id = #{cart.productId}
AND user_id = #{cart.userId}
AND is_delete = 0

mapper.update:
UPDATE cart
SET retail_price = #{retailPrice},
    checked      = #{checked},
    number       = #{number}
WHERE id = #{id}

mapper.select:
SELECT *
FROM cart
WHERE user_id = #{userId}

    AND is_fast = #{isFast}


    AND product_id = #{productId}

AND checked = '1'
AND is_delete = 0
